package org.smart4j.framework;import org.smart4j.framework.helper.BeanHelper;import org.smart4j.framework.helper.ClassHelper;import org.smart4j.framework.helper.ControllerHelper;import org.smart4j.framework.helper.IocHelper;import org.smart4j.framework.util.ClassUtil;/** * @ClassName HelperLoader * @Description TODO * @Author mac * @Date 2019-05-04 06:36 PM * Version 1.0 **/public class HelperLoader {    public static void init(){        /**         * ClassHelper:加载所有的客户端类         * BeanHelper:实例化所有客户端类         *IocHelper:将一个类实例注入另一个类,作为类成员值         *ControllerHelper:获取路径与类方法之间的映射关系         */        Class<?>[] classList = {ClassHelper.class, BeanHelper.class, IocHelper.class, ControllerHelper.class};        for(Class<?> cls : classList){            System.out.println("HelperLoader.init:" + cls.getName());            ClassUtil.loadClass(cls.getName());        }    }}