package org.smart4j.framework.helper;import org.smart4j.framework.annotation.Controller;import org.smart4j.framework.annotation.Service;import org.smart4j.framework.util.ClassUtil;import java.util.HashSet;import java.util.Set;/** * @ClassName ClassHelper * @Description 装载下游类 * @Author mac * @Date 2019-05-04 02:26 PM * Version 1.0 **/public final class ClassHelper {    private static  final Set<Class<?>> CLASS_SET;    /**     * @Author mac     * @Description //TODO 初始化装载AppBasePackage包下的所有包     * @Date 08:40 PM 2019-05-05     * @Param     * @return     **/    static {        String basePackage = ConfigHelper.getAppBasePackage();        CLASS_SET = ClassUtil.getClassSet(basePackage);    }    public static Set<Class<?>> getClassSet(){        return CLASS_SET;    }    public static Set<Class<?>> getServiceClassSet(){        Set<Class<?>> classSet = new HashSet<Class<?>>();        for(Class<?> cls : CLASS_SET){            if(cls.isAnnotationPresent(Service.class)){                classSet.add(cls);            }        }        return classSet;    }    public static Set<Class<?>> getControllerClassSet(){        Set<Class<?>> classSet = new HashSet<Class<?>>();        for(Class<?> cls : CLASS_SET){            if(cls.isAnnotationPresent(Controller.class)){                classSet.add(cls);            }        }        return  classSet;    }    public static Set<Class<?>> getBeanClassSet(){        Set<Class<?>> beanClassSet = new HashSet<Class<?>>();        beanClassSet.addAll(getServiceClassSet());        beanClassSet.addAll(getControllerClassSet());        return beanClassSet;    }}